#include <stdint.h>

// These resource ID types are normally generated by xcbproto into `xproto.h`. They're always 32
// bits.
typedef uint32_t xcb_window_t;
typedef uint32_t xcb_visualid_t;

// This is defined as an opaque type in the real `xcb.h`.
typedef struct xcb_connection_t xcb_connection_t;
